INSERT INTO `news` (`id`, `lang`, `user_id`, `title`, `text`, `photo`, `state`, `date`, `date_create`) VALUES
(37, 'ru', 1, 'Семинар с международным участием по интеллектуальным электрическим сетям', '27 июня 2011 года &nbsp;в  загородном отеле "Виноградово Парк Инн" (Москва) члены Клуба  "Электрополис" приняли участие в Семинаре, посвященном развитию в  России концепции Smart Grid в распределительных сетях. Семинар состоялся при  поддержке журнала "Электроэнергия. Передача и распределение". <br />\r\n<br />\r\nС подробным докладом о  европейском опыте создания интеллектуальных сетей выступил международный  Эксперт г-н Ален Глатини (Франция).  ', '36ac874aac8d617d4a45db17cf4c9db1.jpg', 'active', '2011-06-27', '2011-07-16 23:50:01'),
(38, 'ru', 1, 'Открытие завода компании Schneider Electric', '30 июня 2011 в поселке  Коммунар Ленинградской области состоялось торжественное открытие завода  компании Schneider Electric по производству компактных распределительных  устройств с элегазовой изоляцией серии RM6.  ', 'a207e5999941dff1a196f1ad7fd13ea3.png', 'active', '2011-06-30', '2011-07-16 23:51:23'),
(39, 'ru', 1, 'Научно-технический Совет по инновационному центру "Сколково"', '23 июня 2011 в городе Краснодар состоялось заседание Научно-технического Совета по созданию инфраструктуры и концепции строительства наукограда "Сколково". Президент Клуба "Электрополис" Валерий Саженков выступил с докладом по организации внешнего и внутреннего электроснабжения, а также по энергоэффективным решениям для строящихся объектов Сколково.  ', 'cba6da337677971d20143088cbd12b40.jpg', 'active', '2011-06-23', '2011-07-16 23:52:00'),
(41, 'ru', 1, 'Международная Конференция по распределительным электрическим сетям CIRED-2011', 'С 8 по 11 июня 2011  делегация членов Клуба "Электрополис" посетила международную  Конференцию по распределительным электрическим сетям CIRED-2011 во Франкфурте.<br />\r\n<br />\r\nВ рамках конференции члены  делегации посетили выставку, где были представлены такие ведущие мировые  электротехнические компании, как Schneider Electric, Telvent DMS, Siemens, ABB  и др. <br />\r\n<br />\r\nОсобый интерес вызвала  презентация компании Telvent DMS по автоматизированному управлению гороскими  электрическими сетями в Европе. &nbsp;Делегация посетила электрическую  подстанцию в г. Франкфурт с применением уникального оборудования 35 кВ в  элегазовой изоляции производства Компании Schneider Electric.  ', 'cc80d12ba2ed6e7be9580faf9f392988.jpg', 'active', '2011-06-13', '2011-07-16 23:53:48'),
(42, 'ru', 1, 'Создание сайта', 'В 2011 году состоится одно из важнейших событий в жизни Клуба - создания веб-сайта нашего профессионального сообщества. Сайт станет мощной площадкой для общения, обмена технической информацией и инновационными решениями по развитию распределительных электрических сетей.  ', 'd20c1270b26c10383e6b3e022b9ca0f4.jpg', 'active', '2011-07-19', '2011-07-16 23:54:52'),
