INSERT INTO `pages` (`id`, `lang`, `meta_title`, `meta_description`, `meta_keywords`, `title`, `url`, `text`, `is_published`, `date_create`) VALUES
(1, 'ru', 'Российский Клуб специалистов по распределению электроэнергии «ЭЛЕКТРОПОЛИС»', 'Клуб «ЭЛЕКТРОПОЛИС» - профессиональной дискуссионной платформы для обмена передовыми технологиями.', '', 'ТРАДИЦИИ-ТЕХНОЛОГИИ-ИННОВАЦИИ ', '/', '<div class="subscribe">Под таким девизом в 2006 году был создан первый в России Клуб специалистов по распределению электроэнергии «ЭЛЕКТРОПОЛИС» </div>\r\n<img src="http://el.korolevsait.ru/upload/pages/Fotos/electropolis_03-01.jpg" style="margin-right:10px;float:left" height="250" width="180" /><p>Направление работы Клуба такое: создание профессиональной  дискуссионной платформы для обмена передовыми технологиями и опытом в области  технической политики, эксплуатации и проектирования распределительных сетей в  России и за рубежом. </p>\r\n            <p>В центре внимания работы Клуба находятся проблемы, связанные  с энергосбережением и модернизацией электросетевого комплекса страны. </p>\r\n            <p>В последние годы у сетевиков все заметнее проявляется  интерес к передовому опыту.<br />\r\n              Специалисты внимательно следят за тем, что происходит на  международных сетевых рынках, знакомятся с новым оборудованием и технологиями,  изучают современные методики учета электроэнергии, способы оптимизации затрат и  т.д.</p>\r\n            <p>Задача Клуба - знакомить специалистов с новыми технологиями  и разработками, которые обеспечивают безопасную, надежную и экономичную  доставку электроэнергии от производителя к потребителю. Это, в частности,  автоматизированные системы коммерческого учета электроэнергии (АСКУЭ), системы  диспетчерского управления, релейная защита и автоматика. Внедрение данных  технологий позволяет не только экономить до 30% электроэнергии, но и  существенно упрощают жизнь эксплуатирующим организациям. </p>\r\n            <p>Отдельными темами для дискуссий в Клубе являются  энергосбережение и повышение энергоэффективности работы электрических сетей, а  также проблемы электроснабжения крупных мегаполисов. </p>\r\n            <p>На сегодняшний день Клуб насчитывает около 70 членов.  Членами Клуба являются руководители и технические специалисты ведущих сетевых  предприятий и проектных институтов различных регионов России.</p>  ', 1, '2011-06-25 16:23:15'),
(2, 'ru', 'О клубе Электрополис', 'О клубе Электрополис', 'О клубе Электрополис', 'О клубе Электрополис', '', '<div class="subscribe">Под таким девизом в 2006 году был создан первый в России Клуб специалистов по распределению электроэнергии «ЭЛЕКТРОПОЛИС» </div>\r\n<img src="http://el.korolevsait.ru/upload/pages/Fotos/electropolis_03-01.jpg" style="margin-right:10px;float:left" width="180" height="250" /><p>Направление работы Клуба такое: создание профессиональной  дискуссионной платформы для обмена передовыми технологиями и опытом в области  технической политики, эксплуатации и проектирования распределительных сетей в  России и за рубежом. </p>\r\n            <p>В центре внимания работы Клуба находятся проблемы, связанные  с энергосбережением и модернизацией электросетевого комплекса страны. </p>\r\n            <p>В последние годы у сетевиков все заметнее проявляется  интерес к передовому опыту.<br />\r\n              Специалисты внимательно следят за тем, что происходит на  международных сетевых рынках, знакомятся с новым оборудованием и технологиями,  изучают современные методики учета электроэнергии, способы оптимизации затрат и  т.д.</p>\r\n            <p>Задача Клуба - знакомить специалистов с новыми технологиями  и разработками, которые обеспечивают безопасную, надежную и экономичную  доставку электроэнергии от производителя к потребителю. Это, в частности,  автоматизированные системы коммерческого учета электроэнергии (АСКУЭ), системы  диспетчерского управления, релейная защита и автоматика. Внедрение данных  технологий позволяет не только экономить до 30% электроэнергии, но и  существенно упрощают жизнь эксплуатирующим организациям. </p>\r\n            <p>Отдельными темами для дискуссий в Клубе являются  энергосбережение и повышение энергоэффективности работы электрических сетей, а  также проблемы электроснабжения крупных мегаполисов. </p>\r\n            <p>На сегодняшний день Клуб насчитывает около 70 членов.  Членами Клуба являются руководители и технические специалисты ведущих сетевых  предприятий и проектных институтов различных регионов России.</p>  ', 1, '2011-06-26 14:22:20'),
